sourcesContent:
import React from "react";
import {
  Container,
  Row,
  Col,
  Button,
  ButtonGroup,
  Card,
  CardBody,
  CardFooter,
  DatePicker,
  Fade,
  Form,
  FormInput,
  FormSelect,
  FormRadio,
  FormTextarea,
  InputGroup,
  InputGroupAddon,
  InputGroupText, 
  FormFeedback,
  Badge,
  Alert
} from "shards-react";
import Parse from 'parse';

import FormSectionTitle from "../edit-user-profile/FormSectionTitle";
import CustomFileUpload from "../components-overview/CustomFileUpload";
import CategorySelect from "./CategorySelect"
import IdeaFilterSelect from "./IdeaFilterSelect";
import { all } from "q";
import EvaluationSelect from "./EvaluationSelect";
import ExecutionSelect from "./ExecutionSelect";
import moment from 'moment';
import CompletedIdeaForm from "./CompletedIdeaForm";

import { useTranslation, initReactI18next, withTranslation } from "react-i18next";

const remCharStyle = {
  color: 'green'
};

class ManageIdeaForm extends React.Component {
    constructor(props) {
        super(props);
        const {t} = this.props
        this.state = {
          data:[],
          ideaQuestions: [],
          answers:[],
          category:null,
          department:null,
          date: '',
          categoryQuestions: [],
          filterQuestions: [],
          selectedFilterQ: [],
          filterQAnswers: [],
          categoryQAnswers: [],
          ideaDescription: String,
          file:null, 
          buttonNext:true,
          descriptionValid:'',
          remainingCharacters: 250,
          visible: true,
          filterVisible: false,
          filterQuestionsVisible: false, 
          ideaQuestionsVisible: false,
          hideNextButton: false,
          userName: 'User Name',
          sectionTitle:'',
          formButtonTitle: 'Continuar',
          ideaNumber: '#00008',
          firstActive: false,
          secondActive: false,
          thirdActive: false,
          fourthActive: false,
          progress: [25,75],
          commentTitle: t('COMMENTS_IDEA'),
          submitBtnTitle: "Actualizar IDEA", 
          showCompleted: false,
        }

        this.change = this.change.bind(this);
        this.setCategory = this.setCategory.bind(this);
        this.setDate = this.setDate.bind(this);
        this.setIdeaDescription = this.setIdeaDescription.bind(this);
        this.selectFile = this.selectFile.bind(this);
        this.showNext = this.showNext.bind(this);
        this.addAnswer = this.addAnswer.bind(this);
        this.saveIdea = this.saveIdea.bind(this);
        this.showNext = this.showNext.bind(this);
        this.progressBtnPress = this.progressBtnPress.bind(this);
        this.submitEvaluation = this.submitEvaluation.bind(this);
        this.addToQueue = this.addToQueue.bind(this);
        this.showCompletedIdeaForm = this.showCompletedIdeaForm.bind(this);
    }

    componentDidMount() {
      let currUser = Parse.User.current();
      this.showCorrectScreen();
      this.getUserName(currUser);
      this.getDate();
      this.setPercentage();
    }

    showCorrectScreen() {
      const idea = this.props.idea;
      const percentage = idea.get("progress")[0];
      if (percentage == 100) {
        this.setState({showCompleted: true});
      } else {
        this.setState({showCompleted: false});
      }
    }

    setPercentage() {
      const idea = this.props.idea;
      const percentage = idea.get("progress")[0];
      console.log(percentage);
      switch(percentage) {
        case 25:
          // code block
          console.log('CASE IS 25');
          this.setState({firstActive: true,
            secondActive: false,
            thirdActive: false,
            fourthActive: false,});
          break;
        case 50:
          // code block
          console.log('CASE IS 50');
          this.setState({firstActive: false,
            secondActive: true,
            thirdActive: false,
            fourthActive: false,});
          break;
        case 75:
          // code block
          console.log('CASE IS 75');
          this.setState({firstActive: false,
            secondActive: false,
            thirdActive: true,
            fourthActive: false,});
          break;
        case 100:
          // code block
          console.log('CASE IS 100');
          this.setState({firstActive: false,
            secondActive: false,
            thirdActive: false,
            fourthActive: true,});
          break;
        default:
          // code block
          this.setState({firstActive: false,
            secondActive: false,
            thirdActive: false,
            fourthActive: false,});
          break;
      }
      console.log(this.state.firstActive);
    }

    getDate() {
      this.setState({
        date: new Date()
      });
    }

    async getUserName(user) {
      var query = new Parse.Query(Parse.User);
      query.equalTo("objectId", user.id);
      const results = await query.find();
      const firstName = results[0].get('firstName');
      const lastName = results[0].get('lastName');
      const fullName = firstName + ' ' + lastName;
      this.setState({
        userName: fullName
      });
    }

    handleSubmit() {
      const { formButtonTitle } = this.state;

      if (formButtonTitle == "Continuar") {
        this.showNext();
      } else {
        var isInvalid = false
        this.state.answers.forEach((item,idx) => {
