        alert('Failed to create new object, with error code: ' + error.message);
      });
    }

    progressBtnPress(e) {
      e.preventDefault();
      const value = e.target.value;
      if (value==25) {
        this.setState({
          progress:[25,75],
          firstActive: true,
          secondActive: false,
          thirdActive: false,
          fourthActive: false,
        });
      } else if (value==50) {
        this.setState({
          progress:[50,50],
          firstActive: false,
          secondActive: true,
          thirdActive: false,
          fourthActive: false,
        });
      } else if (value==75) {
        this.setState({
          progress:[75,25],
          firstActive: false,
          secondActive: false,
          thirdActive: true,
          fourthActive: false,
        });
      } else if (value==100) {
        this.setState({
          progress:[100,0],
          firstActive: false,
          secondActive: false,
          thirdActive: false,
          fourthActive: true,
        });
      }
    }

